import React, {useState} from "react";
import Brewery from "./Brewery";
const Type = () => {
    const [data, setData] = useState(Brewery)
    const [breweries, setBreweries] = useState(data);

    const filterResult = (catItem) => {
        const result = Brewery.filter((currentData) => {
            return currentData.type === catItem;
        });
        setData(result);
        setBreweries(result); // Update the breweries state with the filtered data
    }
    return(
        <div id="test">
            <h1 id="typeText">BREWERIES BY SIZE</h1>
            <div id="container">
                <button id="breweryType" onClick={() => filterResult('micro')}>
                    <div id="separateImage">
                        <img id="breweryImage" src="micro.png"/>
                        <h1 id="typeText">Micro Breweries</h1>
                    </div>
                </button>
                <button id="breweryType" onClick={() => filterResult('brewpub')}>
                    <div id="separateImage">
                        <img id="breweryImage" src="regional.png"/>
                        <h1 id="typeText">Brewpub</h1>
                    </div>
                </button>
                <button id="breweryType" onClick={() => filterResult('large')}>
                    <div id="separateImage">
                        <img id="breweryImage" src="macro.png"/>
                        <h1 id="typeText">Macro Breweries</h1>
                    </div>
                </button>
            </div>
            {breweries.map((brewery) => {
                return(
                    <>
                     <Brewery brewery={brewery} />
                    </>
                )
            })}
        </div>
    )
}

export default Type
