import React, { useState, useEffect } from 'react';
import axios from 'axios';


const Brewery = (props) => {
  const [breweries, setBreweries] = useState([]);
  const [filteredBreweries, setFilteredBreweries] = useState([]);

  useEffect(() => {
    const fetchBreweries = async () => {
      const result = await axios.get('https://api.openbrewerydb.org/v1/breweries');
      setBreweries(result.data);
    };
    fetchBreweries();
  }, []);

  const handleFilter = (type) => {
    const filtered = breweries.filter((brewery) => brewery.brewery_type === type);
    setFilteredBreweries(filtered);
  };


  return (
    <div>
      <h1>Breweries</h1>
      <div>
        <button onClick={() => handleFilter('micro')}>Micro</button>
        <button onClick={() => handleFilter('large')}>Large</button>
        <button onClick={() => handleFilter('brewpub')}>Brewpub</button>
      </div>
      <div id="cardLayout">
        <div id="name">
          <div id="nameText">
            <h1>{props.brewery.name}</h1>
          </div>
        </div>
        <div id="info">
          <h4>Type: {props.brewery.brewery_type}</h4>
          <hr />
          <h4>{props.brewery.street}</h4>
          <hr />
          <h4>
            {props.brewery.city}, {props.brewery.state}
          </h4>
          <h4>{props.brewery.country}</h4>
          <hr />
          <div id="website">
            <a href={props.brewery.website_url}>{props.brewery.website_url && props.brewery.website_url.replace(/^https?:\/\//i, '')}</a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Brewery;
